var os = require('os')
var util = require('util')

var system = {
  eol: function() {
    return os.EOL
  },
  arch: function() {
    return os.arch()
  },
  cpus: function() {
    return os.cpus()
  },
  mem: function() {
    return {
      free: os.freemem(),
      total: os.totalmem()
    }
  },
  homedir: function() {
    return os.homedir()
  },
  hostname: function() {
    return os.hostname()
  },
  loadavg: function() {
    return os.loadavg()
  },
  interface: function() {
    return os.networkInterfaces()
  },
  platform: function() {
    return os.platform()
  },
  release: function() {
    return os.release()
  },
  tmpdir: function() {
    return os.tmpdir()
  },
  type: function() {
    return os.type()
  },
  uptime: function() {
    return os.uptime()
  }
}

var fn = function(pathname) {
  var data = system[pathname] ? system[pathname]() : undefined
  if (typeof data === 'undefined') {
    return 'Bad Resource'
  }

  if(util.isObject(data)) {
    return JSON.stringify(data)
  }
  
  return data
}

module.exports = fn